    this.notificationService = notificationService;
    this.notifications = [];
    this.emailLogs = [];
    this.loading = true;
    this.currentPage = 1;
    this.pageSize = 10;
    this.totalItems = 0;
    this.totalPages = 0;
    this.filterType = 'all';
    this.selectedNotification = null;
    this.Math = Math;
    // Stats
    this.totalNotifications = 0;
    this.unreadCount = 0;
    this.readCount = 0;
    // Email modal
    this.showEmailModal = false;
    this.sendingEmail = false;
    this.emailForm = {
      to: '',
      subject: '',
      body: ''
    };
  }
  ngOnInit() {
    this.loadNotifications();
    this.loadEmailLogs();
  }
  loadNotifications() {
    this.loading = true;
    const userId = 'admin'; // In real app, get from Keycloak
    this.notificationService.getNotifications(userId, {
      unreadOnly: this.filterType === 'unread',
      page: this.currentPage,
      limit: +this.pageSize
    }).subscribe({
      next: response => {
        this.notifications = this.filterType === 'read' ? response.data.filter(n => n.isRead) : response.data;
        this.totalItems = response.pagination.total;
        this.totalPages = response.pagination.totalPages;
        this.totalNotifications = response.pagination.total;
        this.unreadCount = response.data.filter(n => !n.isRead).length;
        this.readCount = response.data.filter(n => n.isRead).length;
        this.loading = false;
      },
      error: err => {
        console.error('Failed to load notifications:', err);
        this.loading = false;
      }
    });
  }
  loadEmailLogs() {
    // In a real app, you'd have an endpoint to fetch email logs
    // For now, we'll use mock data
    this.emailLogs = [];
  }
  markAsRead(id) {
    this.notificationService.markAsRead(id).subscribe({
      next: () => {
        this.loadNotifications();
      },
      error: err => {
        console.error('Failed to mark notification as read:', err);
        alert('Failed to mark notification as read');
      }
    });
  }
  markAllAsRead() {
    const unreadNotifications = this.notifications.filter(n => !n.isRead);
    if (unreadNotifications.length === 0) return;
    Promise.all(unreadNotifications.map(n => this.notificationService.markAsRead(n.id).toPromise())).then(() => {
      this.loadNotifications();
    }).catch(err => {
      console.error('Failed to mark all notifications as read:', err);
      alert('Failed to mark all notifications as read');
    });
  }
  viewDetails(notification) {
    this.selectedNotification = notification;
  }
  openEmailModal() {
    this.showEmailModal = true;
    this.emailForm = {
      to: '',
      subject: '',
      body: ''
    };
  }
  closeEmailModal() {
    this.showEmailModal = false;
  }
  sendEmail() {
    this.sendingEmail = true;
    this.notificationService.sendEmail({
      to: this.emailForm.to,
      subject: this.emailForm.subject,
      body: this.emailForm.body
    }).subscribe({
      next: () => {
        this.sendingEmail = false;
        this.closeEmailModal();
        alert('Email sent successfully!');
        this.loadEmailLogs();
      },
      error: err => {
        console.error('Failed to send email:', err);
        this.sendingEmail = false;
        alert('Failed to send email');
      }
    });
  }
  goToPage(page) {
    if (page < 1 || page > this.totalPages) return;
    this.currentPage = page;
    this.loadNotifications();
  }
